import React from 'react'
import './Contact.css'
import { useState } from 'react';
import axios from 'axios'
// import React from "react"; 
import { Container, Row, Col } from "react-bootstrap";
const initialState = {
    name: "",
    email: "",
    phone: "",
    message: "",
    err: "",
    success: "",
  };
  
function Contact() {
    const [formDataUser, setFormDataUser] = useState(initialState);
 

  const {
    name,
    email,
    phone,
    message,
   
  } = formDataUser;

  const handleChange = (e) => {
    //place of do that onChange={(e) => setEmail(e.target.value) for each field (input) we do that
    setFormDataUser({
      ...formDataUser,
      [e.target.name]: e.target.value,
      
    });
  };
  const handleSubmit = async (e) => {
    e.preventDefault(); //default action that belongs to the event will not occur.
    
    try {
          const res = await axios.post("/user/contact", {
          name,
          email,
          phone,
          message
        });
        setFormDataUser({ ...formDataUser});
      }
    catch (err) {
      err.response.data.msg &&
        setFormDataUser({
          ...formDataUser,
          
        });
    }
  };

  return (
    <>
    <Container>
     
    <Row className="mb-5 mt-3">
      <Col lg="8">
        <h1 className="display-4 mb-4">Contact Me</h1>
        <hr className="t_border my-4 ml-0 text-left" />
      </Col>
    </Row>
    <Row className="sec_sp">
      <Col lg="5" className="mb-5">
        <h3 className="color_sec py-4">Get in touch</h3>
        <address>
          <strong>Email:</strong>{" "}
          <a href={`mailto:${contactConfig.YOUR_EMAIL}`}>
            {contactConfig.YOUR_EMAIL}
          </a>
          <br />
          <br />
          {contactConfig.hasOwnProperty("YOUR_FONE") ? (
            <p>
              <strong>Phone:</strong> {contactConfig.YOUR_FONE}
            </p>
          ) : (
            ""
          )}
        </address>
        <p>{contactConfig.description}</p>
      </Col>
      <Col lg="7" className="d-flex align-items-center">
        <form  className="contact__form w-100">
          <Row>
            <Col lg="6" className="form-group">
            <input type="text" name="name" className="form-group"
            onChange={handleChange} />
            </Col>

            <Col lg="6" className="form-group">
            <input type="text" name="email" className="form-group"
            onChange={handleChange} />
            </Col>
            </Row>
            <textarea
                className="form-control rounded-0"
                id="message"
                name="message"
                placeholder="Message"
                rows="5" 
                required
                onChange={handleChange}
              ></textarea>
        <br />
              <Row>
              <Col lg="12" className="form-group">
                  <button className="btn ac_btn" type="submit"> 
                  Send
                  </button>
                </Col>
              </Row>
            </form>
          </Col>
              </Row>
          
              
      </Container>
      </>
  );
}

export default Contact
